const { get_new_item_main,set_biz_item_main,get_helper_main,set_item_data_main } = require('./main.js');
const get_new_item = (data_type,id) => {
    return get_new_item_main(data_type,id);
};
const set_biz_item = async (item,options) => {
    return [error,data] = await set_biz_item_main(item,options);
};
const get_helper = (req) => {
    return data = get_helper_main(req);
};
const set_item_data = (data_type,id,item_data) => {
    return data = set_item_data_main(data_type,id,item_data);
}
module.exports = {
    get_new_item,
    get_helper,
    set_biz_item,
    set_item_data,
};

